import React from 'react'
import ReactDOM from 'react-dom'
import './App.css'

function App() {
  return (
    <div className="App">
      <header id='header'>Compressr</header>
      <QueryForm/>
    </div>
  );
}

class QueryForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {sentences: ""};
    //this.output = ''
    //this.render()
  }

  callAPI() {
    fetch ("http://localhost:8080/api/analysis?text=Hello World!")
    .then(res => res.text())
    .then(sentences => this.setState({sentences: sentences}))
  }

  componentWillMount() {
    this.callAPI()
  }

  SubmitHandler = (event) => {
    event.preventDefault();
    var text = ReactDOM.findDOMNode(this.refs.input).value;

    fetch('http://localhost:8080/api/analysis?text='+text)
    .then(res => res.json())
    .then(sentences => this.setState({sentences: sentences}, () => {
      console.log('fetched these sentences', sentences)
      // for (var sentence of this.state.sentences) {
      //   this.output += sentence
      // }
      // console.log(this.output)
    }))
  }

  buttonHandler = (event) => {
    if (ReactDOM.findDOMNode(this.refs.compress).clicked) {
      this.compressRender()
    } else {
      this.outlineRender()
    }
  }

  render() {
    return (
      <div className="App">
          <div id="inputForm">
            <form onSubmit={this.SubmitHandler}>
              <input ref="compress" type="radio" id="compress" name="type" value="compress"></input>
              <label for="compress">Compress</label>
              <input ref="outline" type="radio" id="outline" name="type" value="outline"></input>
              <label for="outline">Outline</label><br></br>
              <div id="wrapper">
                <input type='submit'/>
                <p>Enter Text Below:</p>
                <textarea ref="input" id="input" type="text"/>
                <textarea ref="output" id="output" type="text">

                </textarea>
              </div>
            </form>
          </div>
      </div>
    )
  }

  compressRender() {
    return (
      <div className="App">
          <div id="inputForm">
            <form onSubmit={this.SubmitHandler}>
              <input ref="compress" type="radio" id="compress" name="type" value="compress"></input>
              <label for="compress">Compress</label>
              <input ref="outline" type="radio" id="outline" name="type" value="outline"></input>
              <label for="outline">Outline</label><br></br>
              <div id="wrapper">
                <input type='submit'/>
                <p>Enter Text Below:</p>
                <textarea ref="input" id="input" type="text"/>
                <textarea ref="output" id="output" type="text">
                  Compressed Text
                </textarea>
              </div>
            </form>
          </div>
      </div>
    )
  }

  outlineRender() {
    return (
      <div className="App">
          <div id="inputForm">
            <form onSubmit={this.SubmitHandler}>
              <input ref="compress" type="radio" id="compress" name="type" value="compress"></input>
              <label for="compress">Compress</label>
              <input ref="outline" type="radio" id="outline" name="type" value="outline"></input>
              <label for="outline">Outline</label><br></br>
              <div id="wrapper">
                <input type='submit'/>
                <p>Enter Text Below:</p>
                <textarea ref="input" id="input" type="text"/>
                <textarea ref="output" id="output" type="text">
                Outlined Text
                </textarea>
              </div>
            </form>
          </div>
      </div>
    )
  }
}



export default App;
