import React from 'react'
import ReactDOM from 'react-dom'
import './App.css'

function App() {
  return (
    <div className="App">
      <header id='header'>Compressr</header>
      <QueryForm/>
    </div>
  );
}

class QueryForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      sentences: "",
      compress: false,
      outline: false
  };
    //this.output = ''
    //this.render()
  }

  callAPI() {
    fetch ("http://localhost:8080/api/analysis?text=Hello World!")
    .then(res => res.text())
    .then(sentences => this.setState({sentences: sentences}))
  }

  componentWillMount() {
    this.callAPI()
  }

  SubmitHandler = (event) => {
    event.preventDefault();
    var text = ReactDOM.findDOMNode(this.refs.input).value;

    fetch('http://localhost:8080/api/analysis?text='+text)
    .then(res => res.json())
    .then(sentences => this.setState({sentences: sentences}, () => {
      console.log('fetched these sentences', sentences)
      // for (var sentence of this.state.sentences) {
      //   this.output += sentence
      // }
      // console.log(this.output)
    }))
  }

  compressButtonHandler = (e) => {
    this.setState({compress: true})
    console.log('compress')
  }

  outlineButtonHandler = (e) => {
    this.setState({compress: false})
    console.log('outline')
  }

  render() {
    if (this.compress) {
      return (
        <div className="App">
            <div id="inputForm">
              <form onSubmit={this.SubmitHandler}>
                <input onChange={this.compressButtonHandler} ref="compress" type="radio" id="compress" name="type" value="compress"></input>
                <label for="compress">Compress</label>
                <input onChange={this.outlineButtonHandler} ref="outline" type="radio" id="outline" name="type" value="outline"></input>
                <label for="outline">Outline</label><br></br>
                <div id="wrapper">
                  <input type='submit'/>
                  <p>Enter Text Below:</p>
                  <textarea ref="input" id="input" type="text"/>
                  <textarea ref="output" id="output" type="text">
                    Compress
                  </textarea>
                </div>
              </form>
            </div>
        </div>
      )
    } 
    if (this.outline) {
      return (
        <div className="App">
            <div id="inputForm">
              <form onSubmit={this.SubmitHandler}>
                <input ref="compress" type="radio" id="compress" name="type" value="compress"></input>
                <label for="compress">Compress</label>
                <input ref="outline" type="radio" id="outline" name="type" value="outline"></input>
                <label for="outline">Outline</label><br></br>
                <div id="wrapper">
                  <input type='submit'/>
                  <p>Enter Text Below:</p>
                  <textarea ref="input" id="input" type="text"/>
                  <textarea ref="output" id="output" type="text">
                    Outline
                  </textarea>
                </div>
              </form>
            </div>
        </div>
      )
    }
    return (
      <div className="App">
          <div id="inputForm">
            <form onSubmit={this.SubmitHandler}>
              <input ref="compress" type="radio" id="compress" name="type" value="compress"></input>
              <label for="compress">Compress</label>
              <input ref="outline" type="radio" id="outline" name="type" value="outline"></input>
              <label for="outline">Outline</label><br></br>
              <div id="wrapper">
                <input type='submit'/>
                <p>Enter Text Below:</p>
                <textarea ref="input" id="input" type="text"/>
                <textarea ref="output" id="output" type="text">
                </textarea>
              </div>
            </form>
          </div>
      </div>
    )
  }
}



export default App;
