import React from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  return (
    <div className="App">
      <header id='header'>Compressr</header>
      <QueryForm/>
    </div>
  );
}

class QueryForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {apiResponse: ""};
  }

  callAPI() {
    fetch ("http://localhost:8080/api/analysis?text=Hello World!")
    .then(res => res.json())
    .then(res => this.setState({apiResponse: res}));
  }

  componentWillMount() {
    this.callAPI();
  }

  render() {
    return (
      <div className="App">
          <div id="inputForm">
            <form onSubmit={this.SubmitHandler}>
              <p>Specify Search Results:</p>
              <p>Channel: <input ref="channel" id="channel" type="text"/></p>
              <p>Number of Videos: <input ref="maxResults" id="maxResults" type="text"/></p>
              <p>Key Terms: <input ref="keyPhrase" id="keyTerms" type="text"/></p>
              <a></a>
              <input type='submit'/>
            </form>
          </div>
          <p>{this.state.apiResponse}</p>
      </div>
    )
  }
}



export default App;
