import React from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  return (
    <div className="App">
      <header id='header'>Compressr</header>
      <QueryForm/>
    </div>
  );
}

class QueryForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {apiResponse: ""};
  }

  callAPI() {
    fetch ("http://localhost:8080/api/analysis?text=Hello World!")
    .then(res => res.text())
    .then(res => this.setState({apiResponse: res}));
  }

  componentWillMount() {
    this.callAPI();
  }

  render() {
    return (
      <div className="App">
          <div id="inputForm">
            <form onSubmit={this.SubmitHandler}>
              <p>Enter Text Below:</p>
              <input ref="text" id="text" type="text"/><br></br>
              <input type="radio" id="compress" name="type" value="compress"></input>
              <label for="compress">Compress</label>
              <input type="radio" id="outline" name="type" value="outline"></input>
              <label for="outline">Outline</label><br></br>
              <a></a>
              <input type='submit'/>
            </form>
          </div>
          <p>{this.state.apiResponse}</p>
      </div>
    )
  }
}



export default App;
