import React from 'react'
import ReactDOM from 'react-dom'
import './App.css'

function App() {
  return (
    <div className="App">
      <header id='header'>Compressr</header>
      <QueryForm/>
    </div>
  );
}

class QueryForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      sentences: [],
      ranks: [],
      words: [],
      metadata: [],
      numOfSentences: 0
  };
    //this.output = ''
    //this.render()
  }

  // callAPI() {
  //   fetch ("http://localhost:8080/api/analysis?text=Hello World!")
  //   .then(res => res.text())
  //   .then(data => this.setState({sentences: data}))
  // }

  // componentWillMount() {
  //   this.callAPI()
  // }

  SubmitHandler = (event) => {
    event.preventDefault();
    var text = ReactDOM.findDOMNode(this.refs.input).value;

    fetch('http://localhost:8080/api/analysis?text='+text)
    .then(res => res.json())
    .then(data => this.setState({sentences: data}, () => {
      //console.log('&&&&&&&&&&&&&&&&&&&&&&&&&FETCHED DATA HERE IS THE  FECTED DATA' + data)
      var tempArray = []
      var inc = 0
      for (const sentence of data.sentences) {
        tempArray.push(sentence.sentence)
        inc++
      }
      this.setState({numOfSentences: inc})
      this.setState({sentences: tempArray})
      tempArray = []

      for (const rank of data.sentences) {
        tempArray.push(rank.rank)
      }
      this.setState({ranks: tempArray})
      tempArray = []

      for (const word of data.entities) {
        tempArray.push(word.word)
      }
      this.setState({words: tempArray})
      tempArray = []

      for (const link of data.entities) {
        tempArray.push(link.link)
      }
      this.setState({metadata: tempArray})
      tempArray = []
    }))
  }

  buttonHandler = (e) => {
    this.setState({value: e.target.value})
    console.log(e.target.value)
  }

  render() {
    var specialSentences = []
    var answerArray = []
    var expectedSpecialSentences = 0
    if (this.state.sentences.length > 2) {
      expectedSpecialSentences = this.state.sentences.length*0.2
      if (expectedSpecialSentences < 2) {
        expectedSpecialSentences = 2
      }
    }
    console.log('NUMBER OF EXPECTED SENTENECS: ' + expectedSpecialSentences)
    console.log('NUMBER OF SENTENECS: ' + this.state.numOfSentences)
    var exists = false
    var rankTolerance = 10.0
    while (specialSentences.length < expectedSpecialSentences) {
      rankTolerance -= 0.2
      console.log(rankTolerance)
      for (const element in this.state.sentences) {
        console.log(this.state.ranks[element])
        if (rankTolerance < this.state.ranks[element]) {
          for (const element of specialSentences) {
            console.log('already in special senteneces : ' + element)
            console.log('this setnence: ' + this.state.sentences[element])
            if (this.state.ranks[element] === element) {
              exists = true
            }
          }
          if (!exists) {
            specialSentences.push(this.state.sentences[element])
            console.log('PUSHED ______ ' + this.state.sentences[element])
            exists = false
          }
        }
      }
    }

    for (const elem in specialSentences) {
      console.log(specialSentences[elem])
    }

    for (const element in this.state.sentences) {
      var isSpecial = false
      for (const specialElement of specialSentences) {
        //console.log(specialElement)
        //console.log(this.state.sentences[element])
        if (specialElement === this.state.sentences[element]) {
          isSpecial = true
          answerArray.push('<mark>' + specialElement + '</mark>')
        }
      }
      if (!isSpecial) {
        answerArray.push(this.state.sentences[element])
      }
    }

    for (const elem in answerArray) {
      console.log(answerArray[elem])
    }
    // const answer = tempArray.map((line, id) => (
    //   <p id={id}>{line}</p>
    // ))
    return (
      <div className="App">
          <link rel="shortcut icon" href="/favicon.ico"></link>
          <div id="inputForm">
            <form onSubmit={this.SubmitHandler}>
              <input onClick={this.buttonHandler} ref="compress" type="radio" id="compress" name="type" value="compress"></input>
              <label for="compress">Compress</label>
              <input onClick={this.buttonHandler} ref="outline" type="radio" id="outline" name="type" value="outline"></input>
              <label for="outline">Outline</label><br></br>
              <input type='submit'/>
            </form>
            <div id="wrapper">
                <p>Enter Text Below:</p>
                <textarea ref="input" id="input" type="text"/>
                <div id="answer">{
                  //answerArray//.map(line => <p>{line.sentence}</p>)
                }</div>
              </div>
          </div>
      </div>
    )
  }
}



export default App;
